    public static int lengthOfLongestSubstringArray(String s) {
        int totalLen = s.length();
        if (s.length() == 0 ||  s.length() == 1)
            return s.length();

        int [] mask = new int[8];
        BiConsumer<int[],Integer> setBit = (a, i) -> {
            int q = i / 32 ;
            int r =  i % 32;
            int shift = r == 0 ? 31 : r -1;
            q = r == 0 ? q -1 : q;
            a[q] = a[q] | 1 << shift;
        };
        BiConsumer<int[],Integer> clearBit = (a, i) -> {
            int q = i / 32 ;
            int r =  i % 32;
            int shift = r == 0 ? 31 : r -1;
            q = r == 0 ? q -1 : q;
            a[q] = a[q] & ~(1 << shift);
        };
        BiPredicate<int[],Integer> isBitSet = (a,i) -> {
            int q = i / 32 ;
            int r =  i % 32;
            int shift = r == 0 ? 31 : r -1;
            q = r == 0 ? q -1 : q;
            return (a[q] & (1<<shift)) == 0;
        };

        int len = 0;
        int maxLen = 0;
        int loopCounter = 0;
        while ( loopCounter < totalLen && len < totalLen) {
            int indx = s.charAt(len);
            // checking if bit for the character is set or not
            if ( isBitSet.test(mask, indx)) {
                // setting the bit.
                setBit.accept(mask, indx);
                maxLen = Math.max(++len - loopCounter, maxLen);
            } else {
                clearBit.accept(mask,(int)s.charAt(loopCounter++));
            }

        }
        return maxLen;
    }
    /*
     * Using Bit Masking.
     * if bit is not set then set it and increment the length of Substring.
     * if already set, i.e character already present.
     * so reset the loopcounter to next character of the previous substring
     * i.e loopConter = loopCount - lengthOfCurrentPreviousSubString +1
     *    set the mask to 0 (clearing all bits) to check for new Substring.
     *
     * NOTE: this solution only assumes that only alphabets will be used, i.e
     *     a-z, hence 4(32) byte mask is used.
     *
     *  Time Complexity : O(n^2)
     */
    public static int lengthOfLongestSubstring(String s) {
        if (s.length() == 0 ||  s.length() == 1)
            return s.length();


        int len = 0;
        int maxLen = 0;
        int mask = 0;
        int loopCounter = 0;
        while ( loopCounter < s.length()) {
            char c = s.charAt(loopCounter);
            int indx = c-'a';
            // checking if bit for the character is set or not
            // mask & (1<< indx)
            if ( (mask & (1<< indx)) == 0 ) {
                // setting the bit.
                mask = mask | (1 << indx);
                maxLen = Math.max(++len, maxLen);
                loopCounter++;
            } else {
               // resetting the counter to next index of the previous SubString.
                loopCounter = loopCounter - len +1 ;
                len = 0;
                mask = 0;
            }


        }
        return maxLen;
    }

    // O(n)
    public static int lengthOfLongestSubstringWithBitsSet(String s) {
        int totalLen = s.length();
        System.out.println("Total Length: "+totalLen);
        if (s.length() == 0 ||  s.length() == 1)
            return s.length();


        int len = 0;
        int maxLen = 0;
        BitSet mask = new BitSet(256);
        int loopCounter = 0;
        int lc = 0;

        while ( loopCounter < totalLen && len < totalLen) {
            int indx = s.charAt(len);
            // checking if bit for the character is set or not
            if ( !mask.get(indx) ) {
                // setting the bit.
                mask.set(indx);
                maxLen = Math.max(++len - loopCounter, maxLen);
            } else {
                mask.clear(s.charAt(loopCounter++));
            }
            lc++;

        }
        System.out.println("total : "+lc);
        return maxLen;
    }
